from machine import Pin, I2C#import timefrom bmp280 import *import ahtx0bus = I2C(1, scl=Pin(22), sda=Pin(21), freq=400000)print(bus.scan())bmp = BMP280(bus, addr = 119)sensor = ahtx0.AHT20(bus)#bmp.use_case(BMP280_CASE_HANDHELD_DYN)#bmp.oversample(BMP280_OS_HIGH)#bmp.temp_os = BMP280_TEMP_OS_16#bmp.press_os = BMP280_PRES_OS_16#bmp.standby = BMP280_STANDBY_125#bmp.iir = BMP280_IIR_FILTER_16def all_print ():  pressure=bmp.pressure  p_bar=pressure/100000  p_mmHg=pressure/133.3224  print('pressure =',pressure)  print('pres bar=',p_bar)  print("Humidity: %0.2f %%" % sensor.relative_humidity)  print('pres p_mmHg=',p_mmHg)  print("\nTemp aht =  %0.2f C" % sensor.temperature)  print('temp bmp = ',bmp.temperature)  h = 16.0  h = 8000* ( (1+ (1/273)) / (bmp.pressure/100000))  k = 8004-237  h = h - k  print("Heigth: %0.2f m " % h)    def height (k=0):  h = bmp.getAltitude()  h = h - k  print("Heigth: %0.2f m " % h)  #return ("Heigth:%0.2fm" % h)  return ("Heigth:%0.2fm" % bmp.getAltitude())  def temp () :  print("\nTemp aht =  %0.2f C" % sensor.temperature)  print('temp bmp = ',bmp.getTemp())  return "Temp aht:%0.1fC  " % sensor.temperature  #,'/r/ntemp bmp=',bmp.temperature  def humidity () :  print("Humidity: %0.2f %%" % sensor.relative_humidity)  return ("Humidity:%0.2f%% " % sensor.relative_humidity)def press () :  print('pres p_mmHg=',bmp.getPress()/133.3224)  #p = 1.2  p = bmp.getPress()/133.3224  return ("Pres:%0.2fmmHg" %p)  