def web_page(gpio_state = ""  ):  msg = gpio_state    html= """<html><body><h1><body bgcolor='Green'>  <form action='/login' method='POST'>Enter setings to connect server <br>  SSID:<input type='text' name='USERNAME' placeholder='WIFI_SSID'><br>  Pas:<input type='password' name='PASSWORD' placeholder='WIFI_pass'><br>  ID : <input type='text' name='ID' placeholder='ID_esp'><br>  ADR:<input type='ADRESS' name='ADRESS' placeholder='ADRESS'><br>  URL:<input type='URL' name='URL' placeholder='URL'><br>  <input type='Submit' name='SUBMIT' value='ENTER'></form>Led """ + msg + """<br>  You can read it  <a href='/inline'>here</a></h1></body></html>  <html><head> <title>ESP Web Server</title> <meta name="viewport" content="width=device-width, initial-scale=1">  <link rel="icon" href="data:,"> <style>html{font-family: Helvetica; display:inline-block; margin: 0px auto; text-align: center;}  h1{color: #0F3376; padding: 2vh;}p{font-size: 1.5rem;}.button{display: inline-block; background-color: #e7bd3b; border: none;   border-radius: 4px; color: white; padding: 16px 40px; text-decoration: none; font-size: 30px; margin: 2px; cursor: pointer;}  .button2{background-color: #4286f4;}</style></head><body>   <p>LED: <strong>""" + gpio_state + """</strong></p><p><a href="/?led=on"><button class="button">ON</button></a></p>  <p><a href="/?led=off"><button class="button button2">OFF</button></a></p></body></html>"""  return html