# wifi conection with timeoutdef do_connect(ssid='Pah1Nah', key='16011986', timeout=5):    print(ssid, key)    import network    import time    import sys        wlan = network.WLAN(network.STA_IF)        # Перевірка чи вже підключено    if wlan.isconnected():        print('Already connected to network')        print('Network config:', wlan.ifconfig())        return wlan.ifconfig()[0]        wlan.active(False)    time.sleep(0.5)    wlan.active(True)    print(f'Connecting to {ssid}...')        # Спроба підключення    wlan.connect(ssid, key)        # Очікування підключення з таймаутом    start_time = time.time()    while not wlan.isconnected():        if time.time() - start_time > timeout:            print('\nConnection failed: Timeout')            wlan.disconnect()            return None                print('.', end='')        time.sleep(1)        print('\nSuccessfully connected!')    print('Network config:', wlan.ifconfig())        return wlan.ifconfig()[0]# Альтернативний запускif __name__ == "__main__":    do_connect()    